//create cue values: either by copying from uploaded text file or randomly generate cue value
function defineCueValues(task, trialNumber, optionNumber, cuesOrder){
  if(task.generateCues){
    var cueVal = new Array(task.cuesNum);
		for (var i = 0; i < cueVal.length; i++){
			var randomNumber = Math.round((Math.random()*100))/100;
			if (randomNumber < task.probabilityPosCue){
				randomNumber = 1;
			} else {
				randomNumber = 0;
			}
			cueVal[i] = randomNumber;
		}
		return shuffle(cueVal);
  } else {
    if (typeof(task.data) != "undefined") {
      let dataTrialCues = task.data[trialNumber - 1];
      let dataTrialOptionCues = dataTrialCues[optionNumber - 1];
      let dispayDataTrialOptionCues = [];
      for (let t = 0; t < cuesOrder.length; t++){
        let index = cuesOrder[t];
        dispayDataTrialOptionCues.push(dataTrialOptionCues[index-1]);
      };
      return(dispayDataTrialOptionCues);
    } 
  }
}

//function to create a trial for the task
const createTrial = (task, trialNumber) => {
  let firstCueValue;
  if (task.firstCuesArray != undefined){
	  firstCueValue = task.firstCuesArray[trialNumber - 1];
	}
  //order of options
  let optionsOrder;
  if (task.whichOptionsOrder == "random"){
    optionsOrder = [];
    for (var i = 1; i <= optionsNum; i++) {
    	optionsOrder.push(i);
    };
    shuffle(optionsOrder);
  } else {
   	optionsOrder = task.optionsOrder;
  }

  //order of cues
  let cuesOrder;
  if (task.whichCuesOrder == "random"){
   	let trialCuesOrder = [];
   	for (var i = 1; i <= cuesNum; i++) {
    	trialCuesOrder.push(i);
    };
    shuffle(trialCuesOrder);
    cuesOrder = trialCuesOrder;
  } else {
   	cuesOrder = task.cuesOrder;
  }

  let displayCuesNames = [];
  for (let t = 0; t < cuesOrder.length; t++){
    var index = cuesOrder[t];
    displayCuesNames.push(task.cuesNames[index-1]);
  }
 
  let options = [];
  let displayNamesInTrial;
  if(task.displayOptionsNames[0] && typeof(task.displayOptionsNames[0]) == "object"){
    displayNamesInTrial = task.displayOptionsNames[trialNumber - 1];
  } else {
    displayNamesInTrial = task.displayOptionsNames;
  }
  
  for (var i = 1; i <= task.optionsNum; i++) {
    let newOption = {
      optionNumber: i, 
      optionName: task.optionsNames[i-1], 
      displayName: displayNamesInTrial[i-1], 
      cueValues: defineCueValues(task, trialNumber, i, cuesOrder)
      };
    options.push(newOption);
  }
  let displayOptions = [];
  for (let t = 0; t < options.length; t++){
    var index = optionsOrder[t];
    displayOptions.push(options[index-1]);
  }
  //randomize
  if(task.randomizeDisplayOptionsNames){
    let shuffledDisplayNames = displayOptions.map(e => e.displayName);
    shuffle(shuffledDisplayNames);
    for (let t = 0; t < displayOptions.length; t++){
      displayOptions[t].displayName = shuffledDisplayNames[t];
    }
  }
  //select correct option
  let correctOption;
  let valueOptions = [];
	for (var i = 0; i < displayOptions.length; i++){
    let displayCuesValidity = [];
    for (let t = 0; t < cuesOrder.length; t++){
      let ind = cuesOrder[t];
      displayCuesValidity.push(task.cuesValidity[ind-1]);
    };
		let valueOption = 0;
		for (let z = 0; z < task.cuesNum; z++){
			let valueOnOneCue = displayCuesValidity[z] * displayOptions[i].cueValues[z];
			valueOption = valueOption + valueOnOneCue;
		}
		valueOptions[i] = Math.round(valueOption * 100)/100;
		//to show option value
		displayOptions[i].optionValue = Math.round(valueOption * 100)/100;
	}
	//to find the maximum
	let max = Math.max.apply(Math, valueOptions);
	let indexMax = getAllIndexes(valueOptions, max);
	if (indexMax.length > 1){
		indexMax = indexMax[Math.floor(Math.random() * indexMax.length)];
	} else {
		indexMax = indexMax[0];
	}
  let displayOptionsNames = displayOptions.map(e => e.displayName);
	correctOption = displayOptionsNames[indexMax];

  return {
    trialNumber: trialNumber,
    cuesNum: task.cuesNum,
    optionsNum: task.optionsNum, 
    displayOptions: displayOptions,
    showOptionsAsImages: task.showOptionsAsImages,
    showCuesAsImages: task.showCuesAsImages,
    correctOption: correctOption,
    cuesOrder: cuesOrder,
    displayCuesNames: displayCuesNames,
    openCues: task.openCues,
    cuesType: task.cuesType,
    paradigm: task.paradigm,
    cueCost: task.cueCost,
    firstCueValue: firstCueValue,
    cuesReaction: task.cuesReaction,
    feedback: task.feedback,
    timeForFeedback: task.timeForFeedback,
    winCost: task.winCost,
    loseCost: task.loseCost,
    instruction: task.instruction,
    calculatedValuesOfOptions: valueOptions,
    theValueOfWinOption: max,
    positionOfWinOption: indexMax,
    startBalance: task.startBalance,
    showPoints: task.showPoints,
  }
}

const upload = (items) => {
