    const array = items.split(';').map(function(item){
      return parseFloat(item.trim());
    });
    return (array);
  }
};

const uploadData = (data) => {
  if(data){
    const trialsArray = data.split(/\s+/);
    const res = trialsArray.map( (trials) => {
      let trial = trials.split(';').map( (t) => {
        return t.trim();
      });
      let options = trial.reduce((memo, value, index) => {
        if(index % study.parameters._numberOfCues == 0 && index !== 0) memo.push([]);
        memo[memo.length - 1].push(value)
        return memo
      },[[]]);
      return (options);
    })
    return (res);
  }
}

