let study = this;

//FUNCTIONS
//prepares the current trial
const prepareTrial = (trial) => {
	createInstructionsHeader(trial);
  createTable(trial);
};

//calculates the points after the choice
const calculatePoints = (trial, answer) => {
	if (answer == String(trial.correctOption)){
		points = points + trial.winCost;
	} else {
		points = points - trial.loseCost;
	}
	if (trial.feedback == true && trial.showPoints == true){
    document.getElementById("points").innerHTML = "Points: " + points;
  }
};

//saves the trial data
const saveTrialData = (trial, answer, answerOption) => {
	study.data.currentTrial = currentTrial;
	study.data.trialTime = Math.round(timeOptionPressed - timeBeginTrial);
	study.data.answer = answer;
	study.data.answerOption = answerOption;
	study.data.points = points;
	if(trial.paradigm == "closed"){
		study.data.infoCost = infoCost;
		study.data.cuesHistory = cuesHistory;
		study.data.cuesValuesHistory = cuesValuesHistory;
		study.data.boxHistory = boxHistory;
		study.data.timeCueHistory = timeCueHistory;
		study.data.numberOfOpenedCues = boxHistory.length;
		const payne = (switchBaseHistory.filter(e => e == "option").length - switchBaseHistory.filter(e => e == "cue").length) / switchBaseHistory.filter(e => e == "option" || e == "cue").length;
		study.data.payneIndex = Math.round(payne * 100)/100;
	};
}

//shows feedback after the choice
const showFeedback = (trial, answer) => {
	var feedback = document.getElementById("feedback");
	feedback.style.visibility = "visible";
	if (answer == String(trial.correctOption)){
		feedback.innerHTML = "Correct";
		feedback.style.color = "green";
	} else {
		feedback.innerHTML = "Wrong";
		feedback.style.color = "red";
	}
	setTimeout(() => { feedback.style.visibility = "hidden"; }, parseInt(thisTrial.timeForFeedback));
}

//shows the instructions above the task
const createInstructionsHeader = (trial) => {
	document.getElementById("instruction").innerHTML = trial.instruction;
	document.getElementById("trialNumber").innerHTML = "The trial number is " + currentTrial;
	if (trial.feedback == true && trial.showPoints == true){
    document.getElementById("points").innerHTML = "Points: " + points;
  }
}

//reacts to the choice (click on a button)
const listenToDecision = () => {
	let decisonButtons = document.querySelectorAll(".OptionsHeadings");
	decisonButtons.forEach( button => button.onclick = function(event){
		if (thisTrial.paradigm == "closed" && cellId != undefined){
      if (thisTrial.cuesReaction == "remainOpen" || thisTrial.cuesReaction == "closeAfterNextClick"){
          cueLeft();
      }
    }
		//debugger;
		const answer = event.target.textContent || event.target.childNodes[0].alt;
  	timeOptionPressed = performance.now();
		let btns = document.querySelectorAll('button');
		btns.forEach(btn => {
			btn.disabled = true;
		});
		calculatePoints(thisTrial, answer);
		if (thisTrial.feedback == true){
   		showFeedback(thisTrial, answer);
   	};
   	saveTrialData(thisTrial, answer, event.target.id);
		//switches to the next trial after the feedback
		setTimeout(() => { study.respond(); }, parseInt(thisTrial.timeForFeedback));
	});
};

//reacts to mouse clicks on the cue values
const closeCues = (trial) => {
	let closedCells = document.querySelectorAll(".CellHolderClosed");
	closedCells.forEach(cell => cell.onclick = function(){
    if (this.id == cellId){
      console.log("The same cell is clicked")
    } else {
      if (firstClick){
        //show the first cue differently
				if (trial.firstCueValue != undefined){
					this.childNodes[0].innerText = trial.firstCueValue;
				}
        cueEntered(trial, this);
        firstClick = false;
      } else {
        if (trial.cuesReaction == "remainOpen"){
      	  cueLeft();
        }
        if (trial.cuesReaction == "closeAfterNextClick"){
          cueLeft();
					let cells = document.querySelectorAll(".Cell");
					cells.forEach(cell => {
						cell.style.visibility = "hidden";
					})
        }
        cueEntered(trial, this);
      }
			this.querySelector('.Cell').style.visibility = "visible";
    }
	});
	//closes the box if the mouse is left (if cuesReaction == "closeAfterBoxIsLeft")
	closedCells.forEach(cell => cell.onmouseleave = function(){
		if (trial.cuesReaction == "closeAfterBoxIsLeft"){
      cueLeft();
      this.querySelector(".Cell").style.visibility = "hidden";
			cellId = undefined;
    }
	})
}

//records the clicked box with the cue value
const cueEntered = (trial, cue) => {
	
	if (cuesHistory && cuesHistory.length > 0){
		const last = cuesHistory[cuesHistory.length - 1].split(';');
		const now = cue.childNodes[0].id.split(';');
		let switchBase;
		if(last[0] == now[0]){
			switchBase = "option";
		} else if (last[1] == now[1]){
			switchBase = "cue";
		} else {
			switchBase = "none";
		}
		switchBaseHistory.push(switchBase);
	}

  cellId = cue.id;
  timeStartLookAtCue = performance.now();
  boxHistory.push(cue.id);
  cuesHistory.push(cue.childNodes[0].id);
  cuesValuesHistory.push(cue.childNodes[0].textContent || cue.childNodes[0].childNodes[0].alt);
  infoCost = infoCost + trial.cueCost;
	if (trial.cueCost != 0){
    document.getElementById("infoCost").innerHTML = "Information costs: " + infoCost;
		points = points - trial.cueCost;
		if (trial.feedback == true){
			document.getElementById("points").innerHTML = "Points: " + points;
		}
  }
}

//records the moment with the cue is left
const cueLeft = () => {
  timeStopLookAtCue = performance.now();
  timeLookingAtCue = Math.round(timeStopLookAtCue - timeStartLookAtCue);
  timeCueHistory.push(timeLookingAtCue);//save time at a cue
}

//creates the tables with options and cues
const createTable = (trial) => {
  var body = document.body;
  tbl  = document.createElement('table');
  tbl.style.width  = '200px';
  tbl.style.height  = '100px';
  tbl.style.border = '1px solid black';
  var counter = 1;
  for(var i = 0; i <= trial.cuesNum; i++){
    var tr = tbl.insertRow();
    for(var j = 0; j <= trial.optionsNum; j++){
      var td = tr.insertCell();
      if (i == 0 & j != 0) {
        var node = document.createElement("BUTTON");        
				if (trial.showOptionsAsImages){
					var img = document.createElement("img");
					var imageName = trial.displayOptions[j-1].displayName;
					img.src = study.options.files[imageName];
					img.alt = trial.displayOptions[j-1].displayName;
					node.style.height = `${150}px`;
					node.appendChild(img);
				} else {
					node.innerHTML = trial.displayOptions[j-1].displayName;
				}
        node.id = trial.displayOptions[j-1].optionName;
        node.className = "OptionsHeadings";
        td.appendChild(node);
      } else if (i != 0 & j == 0) {
      	var node2 = document.createElement("div");

				if (trial.showCuesAsImages){
					var img = document.createElement("img");
					var imageName = trial.displayCuesNames[i-1];
					img.src = study.options.files[imageName];
					img.alt = trial.displayCuesNames[i-1];
					//node.style.height = `${150}px`;
					node2.appendChild(img);
				} else {
					node2.innerHTML = trial.displayCuesNames[i-1];
				}
      
        node2.id = trial.displayCuesNames[i-1];
        node2.className = "CuesHeadings";
        td.appendChild(node2);
      } else if (i != 0 & j != 0){
      	var node3 = document.createElement("div");
        node3.id = counter;
        //to check whether to keep some cells opened or closed
        if(i > trial.openCues) {
        	node3.className = "CellHolderClosed";
        } else {
        	node3.className = "CellHolderOpened";
        }
        var node4 = document.createElement("div");
        node4.id = trial.displayOptions[j-1].optionName + "-" + trial.displayOptions[j-1].displayName + ";" + trial.displayCuesNames[i-1];
     		node4.className = "Cell";
        if (trial.cuesType == "binary") {
        	node4.innerHTML = trial.displayOptions[j-1].cueValues[i-1] > 0 ? "+" : "-";
        } else if (trial.cuesType == "raw"){
        	node4.innerHTML = trial.displayOptions[j-1].cueValues[i-1];
        } else if (trial.cuesType == "image"){
					var img = document.createElement("img");
					var imageName = trial.displayOptions[j-1].cueValues[i-1] > 0 ? study.parameters._imageNamePositive : study.parameters._imageNameNegative;
					img.src = study.options.files[imageName];
					img.alt = trial.displayOptions[j-1].cueValues[i-1];
					img.width = "50";
					node4.appendChild(img);
        } else if(trial.cuesType == "name"){
					var img = document.createElement("img");
					var imageName = trial.displayOptions[j-1].cueValues[i-1];
					img.src = study.options.files[imageName];
					img.alt = trial.displayOptions[j-1].cueValues[i-1];
					img.width = "50";
					node4.appendChild(img);
				} else {
					//may be customized later
				}
        if (trial.paradigm == "closed"){
        	if(i > trial.openCues) {
        		node4.style.visibility = "hidden";
        	}
        } else {
        	node4.style.visibility = "visible";
        }
        node3.appendChild(node4);
        td.appendChild(node3);
        counter++;
      }
      td.style.border = '1px solid black';
      }
    }
    body.appendChild(tbl);

    if (trial.paradigm == "closed"){
			if (trial.cueCost != 0){
				document.getElementById("infoCost").innerHTML = "Information costs: " + infoCost;
			}
			closeCues(trial);
    } else {
      //may be customized for other types of paradigms (e.g., open)
    }
	//enable buttons
	listenToDecision();
	let btns = document.querySelectorAll('button');
	btns.forEach(btn => {
		btn.removeAttribute("disabled");
	});
	//add table to div
  document.querySelector('#tableTask').append(tbl);
	//start measuring the time
	timeBeginTrial = performance.now();
};

